mapper.select:
select 

from WIP_PRL_PART_PLAN_INS_RISK_EXT
where CONTRACT_ID = #{contractId,jdbcType=DECIMAL}
  and PARTITION_NO = #{partitionNo,jdbcType=DECIMAL}
  and RISK_ID = #{riskId,jdbcType=DECIMAL}

select 

from WIP_PRL_PART_PLAN_INS_RISK_EXT
where CONTRACT_ID = #{contractId,jdbcType=DECIMAL}
  and PARTITION_NO = 1
  and THIRD_PART_POLICY_NO = #{thirdPartPolicyNo,jdbcType=VARCHAR}

mapper.delete:
delete from WIP_PRL_PART_PLAN_INS_RISK_EXT
where CONTRACT_ID = #{contractId,jdbcType=DECIMAL}
  and PARTITION_NO = #{partitionNo,jdbcType=DECIMAL}
  and RISK_ID = #{riskId,jdbcType=DECIMAL}
